const std = @import("std");

pub fn main() anyerror!void {
    std.debug.print("const st = [_]u8{{\n", .{});
    for (st) |c| try print_char(c);
    std.debug.print("\n}};\n", .{});
    std.debug.print("{s}", .{st});
}

fn print_char(c: u8) !void {
    var a: [1:0]u8 = undefined; 
    const cc: []const u8 = switch (c) {
        '\n' => "\\n",
        '\\' => "\\\\",
        '\'' => "\\'",
        else => blk: {
            a[0] = c;
            break :blk a[0..];
        },
    };
    std.debug.print("'{s}',", .{cc});
}